static t_discriminant	sphere_discriminant(t_object *obj, t_ray *ray)
{
	t_discriminant	discr;
	t_tuple		sphere_to_ray;

	sphere_to_ray = subtract(ray->origin, obj->origin);
	discr.a = dot(ray->direction, ray->direction);
	discr.b = 2 * dot(ray->direction, sphere_to_ray);
	discr.c = dot(sphere_to_ray, sphere_to_ray) - 1;
	discr.discriminant = discr.b * discr.b - 4 * discr.a * discr.c;
	return (discr);
}

t_intersect_list	*intersect_sphere(t_object *obj, t_ray *ray)
{
	t_ray			transformed_ray;
	t_discriminant	discr;
	t_intersect_list	*xs;
	float			t[2];

	transformed_ray = transform_ray(ray, obj->inverse);
	discr = sphere_discriminant(obj, &transformed_ray);
	if (discr.discriminant < 0)
		return (NULL);
	t[0] = (-discr.b - sqrtf(discr.discriminant)) / (2 * discr.a);
	t[1] = (-discr.b + sqrtf(discr.discriminant)) / (2 * discr.a);
	xs = intersect_list();
	if (!xs)
		return (NULL);
	add_intersection(xs, intersection(t[0], obj));
	add_intersection(xs, intersection(t[1], obj));
	return (xs);
}

