t_tuple	lighting(t_material material, t_light *light, t_comp comp, bool in_shadow)
{
	t_tuple	effective_color;
	t_tuple	ambient;
	t_tuple	diffuse;
	t_tuple	specular;
	t_tuple	lightv;
	t_tuple	reflectv;
	float	light_dot_normal;
	float	reflect_dot_eye;
	float	factor;

	// Combine surface color with light color
	effective_color = hadamard(get_color_at(material, comp.point), light->intensity);
	// Calculate the ambient contribution
	ambient = scalar(effective_color, material.ambient);
	if (in_shadow)
		return (ambient);
	// Calculate direction to light source
	lightv = normalize(subtract(light->position, comp.point));
	// Determine if light is on correct side
	light_dot_normal = dot(lightv, comp.normalv);
	if (light_dot_normal < 0)
	{
		diffuse = color(0, 0, 0);
		specular = color(0, 0, 0);
	}
	else
	{
		// Calculate diffuse contribution
		diffuse = scalar(effective_color, material.diffuse * light_dot_normal);
		// Calculate specular contribution
		reflectv = reflect(negate(lightv), comp.normalv);
		reflect_dot_eye = dot(reflectv, comp.eyev);
		if (reflect_dot_eye <= 0)
			specular = color(0, 0, 0);
		else
		{
			factor = powf(reflect_dot_eye, material.shininess);
			specular = scalar(light->intensity, material.specular * factor);
		}
	}
	return (add(ambient, add(diffuse, specular)));
}

shadow_calculation.code:
bool	is_shadowed(t_world *world, t_tuple point, t_light *light)
{
	t_tuple	v;
	float	distance;
	t_ray	*r;
	t_intersect_list	*intersections;
	t_intersection	*h;

	// Vector from point to light
	v = subtract(light->position, point);
	distance = magnitude(v);
	// Create a ray from point to light
	r = ray(point, normalize(v));
	// Find intersections between point and light
	intersections = intersect_world(world, r);
	// Check if any intersection blocks the light
	h = hit(intersections);
	// Free resources
	free_ray(r);
	free_intersect_list(intersections);
	// Return true if there's a hit before the light source
	return (h != NULL && h->t < distance);
}

ray_generation.code:
t_ray	*ray_for_pixel(t_camera *camera, int px, int py)
{
	float	xoffset;
	float	yoffset;
	float	world_x;
	float	world_y;
	t_tuple	pixel;
	t_tuple	origin;
	t_tuple	direction;
	t_ray	*r;

	// Calculate the offset from the edge of the canvas to the pixel center
	xoffset = (px + 0.5) * camera->pixel_size;
	yoffset = (py + 0.5) * camera->pixel_size;
	// Calculate the world coordinates of the pixel
	world_x = camera->half_width - xoffset;
	world_y = camera->half_height - yoffset;
	// Transform the canvas point and the origin using camera matrix
	pixel = multiply_matrix_tuple(camera->inverse, point(world_x, world_y, -1));
	origin = multiply_matrix_tuple(camera->inverse, point(0, 0, 0));
	// Calculate the ray direction vector and normalize it
	direction = normalize(subtract(pixel, origin));
	// Create the ray
	r = ray(origin, direction);
	return (r);
}

