t_matrix	*translation(float x, float y, float z)
{
	t_matrix	*m;

	m = identity_matrix();
	m->data[0][3] = x;
	m->data[1][3] = y;
	m->data[2][3] = z;
	return (m);
}

t_matrix	*scaling(float x, float y, float z)
{
	t_matrix	*m;

	m = identity_matrix();
	m->data[0][0] = x;
	m->data[1][1] = y;
	m->data[2][2] = z;
	return (m);
}

t_matrix	*view_transform(t_tuple from, t_tuple to, t_tuple up)
{
	t_tuple	forward;
	t_tuple	left;
	t_tuple	true_up;
	t_matrix	*orientation;
	t_matrix	*translation_matrix;
	t_matrix	*result;

	forward = normalize(subtract(to, from));
	left = cross(forward, normalize(up));
	true_up = cross(left, forward);
	orientation = identity_matrix();
	orientation->data[0][0] = left.x;
	orientation->data[0][1] = left.y;
	orientation->data[0][2] = left.z;
	orientation->data[1][0] = true_up.x;
	orientation->data[1][1] = true_up.y;
	orientation->data[1][2] = true_up.z;
	orientation->data[2][0] = -forward.x;
	orientation->data[2][1] = -forward.y;
	orientation->data[2][2] = -forward.z;
	translation_matrix = translation(-from.x, -from.y, -from.z);
	result = multiply_matrices(orientation, translation_matrix);
	free_matrix(orientation);
	free_matrix(translation_matrix);
	return (result);
}

render_pipeline.code:
void	render(t_camera *camera, t_world *world, t_canvas *image)
{
	t_ray	*r;
	t_tuple	color_val;
	int	x;
	int	y;

	y = 0;
	while (y < camera->vsize)
	{
		x = 0;
		while (x < camera->hsize)
		{
			// Generate ray through pixel
			r = ray_for_pixel(camera, x, y);
			// Calculate color at intersection
			color_val = color_at(world, r);
			// Set pixel in canvas
			write_pixel(image, x, y, color_val);
			// Clean up ray
			free_ray(r);
			x++;
		}
		y++;
	}
}

