static t_token	identify_token(char *line)
{
	int		i;
	t_token	token;

	token.type = UNDEFINED;
	token.value = NULL;
	if (!line || line[0] == '\0' || line[0] == '#')
		return (token);
	token.value = trim(line);
	if (!token.value)
		return (token);
	i = 0;
	while (i < NUM_TOKEN_TYPES)
	{
		if (strncmp(token.value, g_token_types[i], 
			strlen(g_token_types[i])) == 0)
		{
			token.type = i;
			break ;
		}
		i++;
	}
	return (token);
}

bool	scan_file(t_world *world, char *filename)
{
	int		fd;
	char	*line;
	t_token	token;

	if (!check_file_extension(filename, ".rt"))
		return (print_error("Invalid file extension"));
	fd = open(filename, O_RDONLY);
	if (fd == -1)
		return (print_error("Could not open file"));
	while (get_next_line(fd, &line))
	{
		token = identify_token(line);
		if (token.type != UNDEFINED)
		{
			if (!process_token(world, token))
				return (false);
		}
		free(token.value);
		free(line);
	}
	close(fd);
	return (post_process_world(world));
}

