t_token *tokenize_input(char *input)
{
	t_token *tokens;
	int i;
	int j;

	tokens = NULL;
	i = 0;
	while (input[i])
	{
		if (is_whitespace(input[i]))
			i++;
		else if (is_operator(input[i]))
		{
			j = get_operator_length(&input[i]);
			add_token(&tokens, create_token(input + i, j, get_token_type(input + i, j)));
			i += j;
		}
		else
		{
			j = get_word_length(&input[i]);
			add_token(&tokens, create_token(input + i, j, WORD));
			i += j;
		}
	}
	return (tokens);
}

command_parsing.code:
t_cmd *parse_command(t_token *tokens)
{
	t_cmd *command;
	t_token *current;

	if (!tokens)
		return (NULL);

	command = init_cmd();
	if (!command)
		return (NULL);

	current = tokens;
	while (current)
	{
		if (current->type == PIPE)
		{
			command->next = parse_command(current->next);
			break;
		}
		else if (is_redirection(current->type))
		{
			if (!handle_redirection(command, current))
			{
				free_cmd(command);
				return (NULL);
			}
			current = current->next->next; // Skip redirection token and filename
		}
		else // Word token (command or argument)
		{
			add_argument(command, current->value);
			current = current->next;
		}
	}

	return (command);
}

builtin_command_handler.code:
int	check_builtin(t_cmd *cmd)
{
	int			i;
	size_t		z;
	static char	tab[7][7] = {"echo", "exit", "cd", "export",
		"unset", "env", "pwd"};

	i = -1;
	if (!cmd || cmd->cmd_array == NULL)
		return (0);
	if ((cmd->cmd_array && ft_strchr(*cmd->cmd_array, '/'))
		|| (cmd->path && ft_strchr(cmd->path, '/')))
		return (0);
	while (++i < 7)
	{
		z = ft_strlen(*cmd->cmd_array);
		if (ft_strlen(tab[i]) > z)
			z = ft_strlen(tab[i]);
		if (!ft_strncmp(*cmd->cmd_array, tab[i], z))
			return (i + 1);
	}
	return (0);
}

int	execute_builtin(t_cmd *cmd, int type)
{
	int	i;

	i = 0;
	if (type == 1)
		i = ft_echo(cmd);
	else if (type == 2)
		i = ft_exit(cmd);
	else if (type == 3)
		i = ft_cd(cmd);
	else if (type == 4)
		i = ft_export(cmd);
	else if (type == 5)
		i = ft_unset(cmd);
	else if (type == 6)
		i = ft_env(cmd);
	else if (type == 7)
		i = ft_pwd(cmd);
	return (i);
}

command_execution.code:
int	execute_command(t_cmd *cmd)
{
	int	type;
	int	status;
	pid_t	pid;

	type = check_builtin(cmd);
	if (is_parent_builtin(type) && !cmd->next && cmd->prev == NULL)
		return (execute_builtin(cmd, type));

	signal(SIGINT, SIG_IGN);
	signal(SIGQUIT, SIG_IGN);
	pid = fork();
	if (pid == -1)
		return (print_error("fork", NULL, 1));
	if (pid == 0)
	{
		signal(SIGINT, SIG_DFL);
		signal(SIGQUIT, SIG_DFL);
		process_redirections(cmd);
		if (cmd->prev)
			dup2(cmd->prev->pipe_fd[0], STDIN_FILENO);
		if (cmd->next)
			dup2(cmd->pipe_fd[1], STDOUT_FILENO);
		close_pipes(cmd);
		if (type)
			exit(execute_builtin(cmd, type));
		else if (cmd->path)
			execve(cmd->path, cmd->cmd_array, get_env_array());
		exit(127);
	}
	if (cmd->prev)
		close(cmd->prev->pipe_fd[0]);
	if (cmd->next)
		close(cmd->pipe_fd[1]);
	waitpid(pid, &status, 0);
	return (WEXITSTATUS(status));
}

