void	handler_sigint(int sig)
{
	(void)sig;
	g_ms_data.exit_status = 128 + SIGINT;
	write(2, "\n", 1);
	rl_replace_line("", 0);
	rl_on_new_line();
	rl_redisplay();
}

void	handler_sigquit(int sig)
{
	(void)sig;
}

int	set_interactive_signals(void)
{
	struct sigaction	sa_int;
	struct sigaction	sa_quit;

	sigemptyset(&sa_int.sa_mask);
	sigemptyset(&sa_quit.sa_mask);
	sa_int.sa_handler = handler_sigint;
	sa_int.sa_flags = 0;
	sa_quit.sa_handler = handler_sigquit;
	sa_quit.sa_flags = 0;
	if (sigaction(SIGINT, &sa_int, NULL) == -1
		|| sigaction(SIGQUIT, &sa_quit, NULL) == -1)
		return (print_error("sigaction", NULL, 1));
	return (0);
}

int	set_noninteractive_signals(void)
{
	signal(SIGINT, SIG_DFL);
	signal(SIGQUIT, SIG_DFL);
	return (0);
}

environment_variable_management.code:
t_env	*create_env_node(char *key, char *value)
{
	t_env	*node;

	node = malloc(sizeof(t_env));
	if (!node)
		return (NULL);
	node->key = ft_strdup(key);
	if (!node->key)
	{
		free(node);
		return (NULL);
	}
	node->value = NULL;
	if (value)
	{
		node->value = ft_strdup(value);
		if (!node->value)
		{
			free(node->key);
			free(node);
			return (NULL);
		}
	}
	node->next = NULL;
	return (node);
}

void	env_add(t_env **env_list, char *key, char *value)
{
	t_env	*node;
	t_env	*current;

	if (!env_list || !key)
		return ;
	if (*env_list)
	{
		current = find_env(*env_list, key);
		if (current)
		{
			if (current->value)
				free(current->value);
			current->value = value ? ft_strdup(value) : NULL;
			return ;
		}
	}
	node = create_env_node(key, value);
	if (!node)
		return ;
	if (!*env_list)
		*env_list = node;
	else
	{
		current = *env_list;
		while (current->next)
			current = current->next;
		current->next = node;
	}
}

heredoc_handling.code:
int	heredoc(t_cmd *cmd, char *delim)
{
	char	*line;
	int		fd[2];

	if (pipe(fd) == -1)
		return (-1);
	while (1)
	{
		line = readline("> ");
		if (!line || !ft_strcmp(line, delim))
		{
			free(line);
			break;
		}
		write(fd[1], line, ft_strlen(line));
		write(fd[1], "\n", 1);
		free(line);
	}
	close(fd[1]);
	if (cmd->input_fd != STDIN_FILENO)
		close(cmd->input_fd);
	cmd->input_fd = fd[0];
	return (0);
}

redirection_handling.code:
int	process_redirections(t_cmd *cmd)
{
	int	i;

	i = -1;
	if (cmd->input_fd != STDIN_FILENO)
	{
		if (dup2(cmd->input_fd, STDIN_FILENO) == -1)
			return (print_error("dup2", NULL, 1));
		close(cmd->input_fd);
	}
	if (cmd->output_fd != STDOUT_FILENO)
	{
		if (dup2(cmd->output_fd, STDOUT_FILENO) == -1)
			return (print_error("dup2", NULL, 1));
		close(cmd->output_fd);
	}
	while (cmd->fds && cmd->fds[++i] > 2)
		close(cmd->fds[i]);
	return (0);
}

int	open_input_file(t_cmd *cmd, char *file)
{
	int	fd;

	fd = open(file, O_RDONLY);
	if (fd == -1)
		return (print_error(file, NULL, 1));
	if (cmd->input_fd != STDIN_FILENO)
		close(cmd->input_fd);
	cmd->input_fd = fd;
	cmd->fds = add_fd(cmd->fds, fd);
	return (0);
}

int	open_output_file(t_cmd *cmd, char *file, int append)
{
	int	fd;
	int	flags;

	flags = O_WRONLY | O_CREAT;
	if (append)
		flags |= O_APPEND;
	else
		flags |= O_TRUNC;
	fd = open(file, flags, 0644);
	if (fd == -1)
		return (print_error(file, NULL, 1));
	if (cmd->output_fd != STDOUT_FILENO)
		close(cmd->output_fd);
	cmd->output_fd = fd;
	cmd->fds = add_fd(cmd->fds, fd);
	return (0);
}

