export class GameEngine {
  constructor(gameState, settings) {
    this.state = gameState;
    this.settings = settings;
    this.lastUpdate = Date.now();
  }

  update() {
    const now = Date.now();
    const deltaTime = (now - this.lastUpdate) / 1000; // Convert to seconds
    this.lastUpdate = now;

    // Update ball position
    this.state.ball.x += this.state.ball.velocityX * deltaTime;
    this.state.ball.y += this.state.ball.velocityY * deltaTime;

    // Check for collisions with top and bottom walls
    if (this.state.ball.y <= 0 || this.state.ball.y >= this.settings.canvasHeight) {
      this.state.ball.velocityY = -this.state.ball.velocityY;
      this.state.ball.y = Math.max(0, Math.min(this.state.ball.y, this.settings.canvasHeight));
    }

    // Check for collisions with paddles
    this.checkPaddleCollision();

    // Check for scoring
    this.checkScoring();

    return this.state;
  }

  checkPaddleCollision() {
    // Left paddle collision
    if (this.state.ball.velocityX < 0 &&
        this.state.ball.x <= this.settings.paddleWidth &&
        this.state.ball.y >= this.state.leftPaddle.y &&
        this.state.ball.y <= this.state.leftPaddle.y + this.settings.paddleHeight) {

      // Calculate bounce angle based on where the ball hit the paddle
      const hitPosition = (this.state.ball.y - this.state.leftPaddle.y) / this.settings.paddleHeight;
      const bounceAngle = (hitPosition - 0.5) * Math.PI / 3; // -/6 to /6 range

      // Update velocity with increased speed
      const speed = Math.sqrt(Math.pow(this.state.ball.velocityX, 2) + Math.pow(this.state.ball.velocityY, 2));
      const newSpeed = speed * 1.05; // Increase speed by 5%

      this.state.ball.velocityX = Math.cos(bounceAngle) * newSpeed;
      this.state.ball.velocityY = Math.sin(bounceAngle) * newSpeed;
    }

    // Right paddle collision (similar logic)
    if (this.state.ball.velocityX > 0 &&
        this.state.ball.x >= this.settings.canvasWidth - this.settings.paddleWidth &&
        this.state.ball.y >= this.state.rightPaddle.y &&
        this.state.ball.y <= this.state.rightPaddle.y + this.settings.paddleHeight) {

      const hitPosition = (this.state.ball.y - this.state.rightPaddle.y) / this.settings.paddleHeight;
      const bounceAngle = (hitPosition - 0.5) * Math.PI / 3; // -/6 to /6 range

      const speed = Math.sqrt(Math.pow(this.state.ball.velocityX, 2) + Math.pow(this.state.ball.velocityY, 2));
      const newSpeed = speed * 1.05; // Increase speed by 5%

      this.state.ball.velocityX = -Math.cos(bounceAngle) * newSpeed;
      this.state.ball.velocityY = Math.sin(bounceAngle) * newSpeed;
    }
  }

  checkScoring() {
    // Ball out of bounds on left side
    if (this.state.ball.x < 0) {
      this.state.scores.right += 1;
      this.resetBall('right');
    }

    // Ball out of bounds on right side
    if (this.state.ball.x > this.settings.canvasWidth) {
      this.state.scores.left += 1;
      this.resetBall('left');
    }
  }

  resetBall(direction) {
    this.state.ball.x = this.settings.canvasWidth / 2;
    this.state.ball.y = this.settings.canvasHeight / 2;

    const angle = ((Math.random() * Math.PI / 4) - Math.PI / 8) + (direction === 'left' ? Math.PI : 0);
    const speed = this.settings.initialBallSpeed;

    this.state.ball.velocityX = Math.cos(angle) * speed;
    this.state.ball.velocityY = Math.sin(angle) * speed;
  }
}

