import json
import asyncio
from channels.generic.websocket import AsyncWebsocketConsumer
from asgiref.sync import sync_to_async
from django.db import transaction
from .models import Game, GamePlayer, User
from .game_engine import GameEngine

class GameConsumer(AsyncWebsocketConsumer):
    async def connect(self):
        self.game_id = self.scope['url_route']['kwargs']['game_id']
        self.game_group_name = f'game_{self.game_id}'
        self.user = self.scope['user']

        # Join game group
        await self.channel_layer.group_add(
            self.game_group_name,
            self.channel_name
        )

        # Accept the connection
        await self.accept()

        # Check if user is a player or spectator
        is_player = await self.is_game_player()
        if is_player:
            await self.send(text_data=json.dumps({
                'type': 'game_joined',
                'role': 'player',
                'player_id': str(self.user.id)
            }))

            # Get or initialize game state
            game_state = await self.get_game_state()
            await self.channel_layer.group_send(
                self.game_group_name,
                {
                    'type': 'game_state_update',
                    'state': game_state
                }
            )
        else:
            await self.send(text_data=json.dumps({
                'type': 'game_joined',
                'role': 'spectator'
            }))

    async def disconnect(self, close_code):
        # Leave game group
        await self.channel_layer.group_discard(
            self.game_group_name,
            self.channel_name
        )

        # Handle player disconnection
        is_player = await self.is_game_player()
        if is_player:
            await self.handle_player_disconnect()

    async def receive(self, text_data):
        data = json.loads(text_data)
        message_type = data.get('type')

        if message_type == 'paddle_move':
            # Validate user is a player
            is_player = await self.is_game_player()
            if not is_player:
                return

            # Update paddle position
            position = data.get('position')
            await self.update_paddle_position(position)

            # Broadcast the updated position
            await self.channel_layer.group_send(
                self.game_group_name,
                {
                    'type': 'paddle_position',
                    'player_id': str(self.user.id),
                    'position': position
                }
            )

        elif message_type == 'start_game':
            # Only host can start game
            is_host = await self.is_game_host()
            if is_host:
                await self.start_game()

    async def game_state_update(self, event):
        # Send game state to the client
        await self.send(text_data=json.dumps({
            'type': 'game_state',
            'state': event['state']
        }))

    async def paddle_position(self, event):
        # Send paddle position update to clients
        await self.send(text_data=json.dumps({
            'type': 'paddle_position',
            'player_id': event['player_id'],
            'position': event['position']
        }))

    @sync_to_async
    def is_game_player(self):
        try:
            return GamePlayer.objects.filter(game_id=self.game_id, user=self.user).exists()
        except Exception:
            return False

    @sync_to_async
    def is_game_host(self):
        try:
            return Game.objects.filter(id=self.game_id, host=self.user).exists()
        except Exception:
            return False

    @sync_to_async
    def get_game_state(self):
        game = Game.objects.get(id=self.game_id)
        # Construct and return game state
        return {
            'id': str(game.id),
            'status': game.status,
            'players': list(game.gameplayer_set.values('user__id', 'user__username', 'side')),
            'scores': {'left': game.score_left, 'right': game.score_right},
            'ball': {'x': game.ball_x, 'y': game.ball_y},
            'leftPaddle': {'y': game.left_paddle_y},
            'rightPaddle': {'y': game.right_paddle_y}
        }

    @sync_to_async
    def update_paddle_position(self, position):
        with transaction.atomic():
            game = Game.objects.get(id=self.game_id)
            player = game.gameplayer_set.get(user=self.user)

            if player.side == 'left':
                game.left_paddle_y = position
            else:
                game.right_paddle_y = position
            game.save(update_fields=['left_paddle_y', 'right_paddle_y'])

    @sync_to_async
    def handle_player_disconnect(self):
        with transaction.atomic():
            game = Game.objects.get(id=self.game_id)
            if game.status == 'playing':
                # Forfeit the game
                game.status = 'finished'
                player = game.gameplayer_set.get(user=self.user)

                if player.side == 'left':
                    game.score_right = game.max_score  # Right player wins
                else:
                    game.score_left = game.max_score  # Left player wins

                game.save()

    async def start_game(self):
        # Initialize and start the game
        game_state = await self.initialize_game()

        # Notify all clients that the game is starting
        await self.channel_layer.group_send(
            self.game_group_name,
            {
                'type': 'game_state_update',
                'state': game_state
            }
        )

        # Start the game loop
        asyncio.create_task(self.game_loop())

    @sync_to_async
    def initialize_game(self):
        with transaction.atomic():
            game = Game.objects.get(id=self.game_id)
            game.status = 'playing'
            game.ball_x = 400  # Center X
            game.ball_y = 300  # Center Y
            game.ball_velocity_x = 200  # Initial X velocity
            game.ball_velocity_y = 0  # Initial Y velocity
            game.save()

            return self.get_game_state_sync(game)

    def get_game_state_sync(self, game):
        # Non-async version for use within @sync_to_async methods
        return {
            'id': str(game.id),
            'status': game.status,
            'players': [{'user_id': p.user.id, 'username': p.user.username, 'side': p.side} 
                       for p in game.gameplayer_set.select_related('user').all()],
            'scores': {'left': game.score_left, 'right': game.score_right},
            'ball': {'x': game.ball_x, 'y': game.ball_y, 
                    'velocityX': game.ball_velocity_x, 'velocityY': game.ball_velocity_y},
            'leftPaddle': {'y': game.left_paddle_y},
            'rightPaddle': {'y': game.right_paddle_y}
        }

    async def game_loop(self):
        # Main game loop, runs until game is finished
        engine = GameEngine()

        while True:
            # Get current game state
            game = await self.get_game_sync()

            if game.status != 'playing':
                break

            # Update game state using physics engine
            game_state = self.get_game_state_sync(game)
            updated_state = engine.update(game_state)

            # Save updated state to database
            await self.save_game_state(updated_state)

            # Broadcast updated state to all clients
            await self.channel_layer.group_send(
                self.game_group_name,
                {
                    'type': 'game_state_update',
                    'state': updated_state
                }
            )

            # Check for win condition
            if updated_state['scores']['left'] >= game.max_score or \
               updated_state['scores']['right'] >= game.max_score:
                await self.end_game()
                break

            # Wait before next update (60 FPS)
            await asyncio.sleep(1/60)

    @sync_to_async
    def get_game_sync(self):
        return Game.objects.get(id=self.game_id)

    @sync_to_async
    def save_game_state(self, state):
        with transaction.atomic():
            game = Game.objects.get(id=self.game_id)
            game.ball_x = state['ball']['x']
            game.ball_y = state['ball']['y']
            game.ball_velocity_x = state['ball']['velocityX']
            game.ball_velocity_y = state['ball']['velocityY']
            game.score_left = state['scores']['left']
            game.score_right = state['scores']['right']
            game.save()

    @sync_to_async
    def end_game(self):
        with transaction.atomic():
            game = Game.objects.get(id=self.game_id)
            game.status = 'finished'
            game.save()

            # Update player stats, ELO, etc.
            self.update_player_stats(game)

    def update_player_stats(self, game):
        # Update player statistics based on game results
        left_player = game.gameplayer_set.get(side='left').user
        right_player = game.gameplayer_set.get(side='right').user

        # Determine winner
        if game.score_left > game.score_right:
            winner = left_player
            loser = right_player
        else:
            winner = right_player
            loser = left_player

        # Update statistics
        winner.profile.games_won += 1
        loser.profile.games_lost += 1
        winner.profile.save()
        loser.profile.save()

        # Could implement ELO rating system here

