import pyotp
import qrcode
import io
import base64
from django.conf import settings
from django.contrib.auth import get_user_model
from rest_framework import status, viewsets, permissions
from rest_framework.response import Response
from rest_framework.decorators import action
from .models import UserProfile

User = get_user_model()

class TwoFactorAuthViewSet(viewsets.ViewSet):
    permission_classes = [permissions.IsAuthenticated]

    @action(detail=False, methods=['post'])
    def enable(self, request):
        """Enable 2FA for the authenticated user"""
        user = request.user
        profile = UserProfile.objects.get(user=user)

        # Check if 2FA is already enabled
        if profile.two_factor_enabled:
            return Response({'error': '2FA is already enabled'}, 
                            status=status.HTTP_400_BAD_REQUEST)

        # Generate a new secret key
        secret_key = pyotp.random_base32()
        profile.two_factor_secret = secret_key
        profile.save(update_fields=['two_factor_secret'])

        # Generate provisioning URI for QR code
        totp = pyotp.TOTP(secret_key)
        provisioning_uri = totp.provisioning_uri(
            name=user.email,
            issuer_name=settings.TWO_FACTOR_ISSUER
        )

        # Generate QR code image
        qr = qrcode.QRCode(
            version=1,
            error_correction=qrcode.constants.ERROR_CORRECT_L,
            box_size=10,
            border=4,
        )
        qr.add_data(provisioning_uri)
        qr.make(fit=True)

        img = qr.make_image(fill_color="black", back_color="white")
        buffer = io.BytesIO()
        img.save(buffer, format="PNG")
        qr_code_base64 = base64.b64encode(buffer.getvalue()).decode()

        return Response({
            'secret': secret_key,
            'qr_code': f'data:image/png;base64,{qr_code_base64}'
        })

    @action(detail=False, methods=['post'])
    def verify(self, request):
        """Verify and activate 2FA with provided token"""
        user = request.user
        profile = UserProfile.objects.get(user=user)
        token = request.data.get('token')

        if not token:
            return Response({'error': 'Token is required'}, 
                            status=status.HTTP_400_BAD_REQUEST)

        # Verify the token
        totp = pyotp.TOTP(profile.two_factor_secret)
        if totp.verify(token):
            profile.two_factor_enabled = True
            profile.save(update_fields=['two_factor_enabled'])
            return Response({'success': '2FA has been enabled'})
        else:
            return Response({'error': 'Invalid token'}, 
                            status=status.HTTP_400_BAD_REQUEST)

    @action(detail=False, methods=['post'])
    def disable(self, request):
        """Disable 2FA for the authenticated user"""
        user = request.user
        profile = UserProfile.objects.get(user=user)
        token = request.data.get('token')

        # Verify user with current token before disabling
        if not profile.two_factor_enabled:
            return Response({'error': '2FA is not enabled'}, 
                            status=status.HTTP_400_BAD_REQUEST)

        if not token:
            return Response({'error': 'Token is required'}, 
                            status=status.HTTP_400_BAD_REQUEST)

        # Verify the token
        totp = pyotp.TOTP(profile.two_factor_secret)
        if totp.verify(token):
            profile.two_factor_enabled = False
            profile.two_factor_secret = ""
            profile.save(update_fields=['two_factor_enabled', 'two_factor_secret'])
            return Response({'success': '2FA has been disabled'})
        else:
            return Response({'error': 'Invalid token'}, 
                            status=status.HTTP_400_BAD_REQUEST)

    @action(detail=False, methods=['post'])
    def validate(self, request):
        """Validate a token for a user during login"""
        user_id = request.data.get('user_id')
        token = request.data.get('token')

        if not user_id or not token:
            return Response({'error': 'User ID and token are required'}, 
                            status=status.HTTP_400_BAD_REQUEST)

        try:
            user = User.objects.get(id=user_id)
            profile = UserProfile.objects.get(user=user)

            if not profile.two_factor_enabled:
                return Response({'error': '2FA is not enabled for this user'},
                                status=status.HTTP_400_BAD_REQUEST)

            # Verify the token
            totp = pyotp.TOTP(profile.two_factor_secret)
            if totp.verify(token):
                return Response({'success': True})
            else:
                return Response({'error': 'Invalid token'},
                                status=status.HTTP_400_BAD_REQUEST)

        except User.DoesNotExist:
            return Response({'error': 'User not found'},
                            status=status.HTTP_404_NOT_FOUND)
        except UserProfile.DoesNotExist:
            return Response({'error': 'User profile not found'},
                            status=status.HTTP_404_NOT_FOUND)

