export class AIOpponent {
  constructor(difficulty = 'medium', gameState, settings) {
    this.difficulty = difficulty; // 'easy', 'medium', 'hard'
    this.gameState = gameState;
    this.settings = settings;
    this.paddleHeight = settings.paddleHeight;
    this.canvasHeight = settings.canvasHeight;
    this.reactionDelay = this._getReactionDelay();
    this.lastMoveTime = Date.now();
    this.targetPosition = null;

    // Error margin for hard AI not being perfect
    this.predictionError = this._getPredictionError();

    // For easy AI: randomness in movement
    this.randomFactor = this._getRandomFactor();
    this.lastRandomChange = Date.now();
    this.randomChangeInterval = 500; // Change direction every 500ms for easy AI

    // For medium AI: how far ahead to look
    this.lookAheadFactor = this._getLookAheadFactor();
  }

  _getReactionDelay() {
    // Delay in milliseconds before AI reacts
    switch (this.difficulty) {
      case 'easy': return 300;
      case 'medium': return 150;
      case 'hard': return 30;
      default: return 150;
    }
  }

  _getPredictionError() {
    // Error margin in prediction (pixels)
    switch (this.difficulty) {
      case 'easy': return 60;
      case 'medium': return 20;
      case 'hard': return 5;
      default: return 20;
    }
  }

  _getRandomFactor() {
    // Random movement factor for easy AI
    switch (this.difficulty) {
      case 'easy': return 0.7;
      case 'medium': return 0.3;
      case 'hard': return 0;
      default: return 0.3;
    }
  }

  _getLookAheadFactor() {
    // How far ahead in time the AI predicts (milliseconds)
    switch (this.difficulty) {
      case 'easy': return 100;
      case 'medium': return 500;
      case 'hard': return 1000;
      default: return 500;
    }
  }

  update() {
    const ball = this.gameState.ball;
    let paddleY;

    // Determine which paddle to control based on the ball's direction
    if (ball.velocityX > 0) {
      // Ball is moving toward right paddle
      paddleY = this.gameState.rightPaddle.y;
    } else {
      // Ball is moving toward left paddle
      paddleY = this.gameState.leftPaddle.y;
    }

    const now = Date.now();

    // Easy AI: Add randomness periodically
    if (this.difficulty === 'easy' && now - this.lastRandomChange > this.randomChangeInterval) {
      this.lastRandomChange = now;
      if (Math.random() < this.randomFactor) {
        // Move randomly sometimes
        return Math.random() * this.canvasHeight;
      }
    }

    // Calculate where the ball will intersect with the paddle's x-coordinate
    if (now - this.lastMoveTime > this.reactionDelay) {
      this.lastMoveTime = now;
      this.targetPosition = this._predictBallPosition();

      // Add error based on difficulty
      const error = (Math.random() * 2 - 1) * this.predictionError;
      this.targetPosition = Math.max(0, Math.min(this.canvasHeight - this.paddleHeight, this.targetPosition + error));
    }

    // Return current target position
    return this.targetPosition;
  }

  _predictBallPosition() {
    const ball = this.gameState.ball;
    const paddleX = ball.velocityX > 0 ? this.settings.canvasWidth - this.settings.paddleWidth : this.settings.paddleWidth;

    // If ball is moving away from this paddle, just track the center
    if ((ball.velocityX > 0 && paddleX < ball.x) || (ball.velocityX < 0 && paddleX > ball.x)) {
      return (this.canvasHeight - this.paddleHeight) / 2;
    }

    // Calculate time until ball reaches paddle
    const distanceToTravel = Math.abs(paddleX - ball.x);
    const timeToReach = distanceToTravel / Math.abs(ball.velocityX);

    // Predict position based on difficulty level
    const lookAheadTime = Math.min(timeToReach, this.lookAheadFactor / 1000);

    // Calculate where ball will be after look-ahead time
    let futureY = ball.y + ball.velocityY * lookAheadTime;

    // Account for bounces off top and bottom walls
    const bounceCount = Math.floor(futureY / this.settings.canvasHeight);
    if (bounceCount % 2 === 0) {
      futureY = futureY % this.settings.canvasHeight;
    } else {
      futureY = this.settings.canvasHeight - (futureY % this.settings.canvasHeight);
    }

    // Return position, adjusted to center paddle on ball
    return Math.max(0, Math.min(this.canvasHeight - this.paddleHeight, futureY - this.paddleHeight / 2));
  }

  // Advanced prediction for hard difficulty
  _predictBallPath() {
    if (this.difficulty !== 'hard') return this._predictBallPosition();

    const ball = this.gameState.ball;
    const paddleX = ball.velocityX > 0 ? this.settings.canvasWidth - this.settings.paddleWidth : this.settings.paddleWidth;

    // Clone ball properties to simulate future movement
    let simulatedBall = {
      x: ball.x,
      y: ball.y,
      velocityX: ball.velocityX,
      velocityY: ball.velocityY
    };

    // Simulate ball movement until it reaches paddle X position
    while ((ball.velocityX > 0 && simulatedBall.x < paddleX) || 
           (ball.velocityX < 0 && simulatedBall.x > paddleX)) {
      // Update simulated ball position
      simulatedBall.x += simulatedBall.velocityX * 0.016; // Assuming 60 FPS (16ms)
      simulatedBall.y += simulatedBall.velocityY * 0.016;

      // Check for collision with top/bottom walls
      if (simulatedBall.y <= 0 || simulatedBall.y >= this.settings.canvasHeight) {
        simulatedBall.velocityY *= -1; // Reverse Y velocity on collision
      }
    }

    // Return predicted position, adjusted to center paddle on ball
    return Math.max(0, Math.min(this.canvasHeight - this.paddleHeight, simulatedBall.y - this.paddleHeight / 2));
  }
}

