export class GameState {
  constructor(canvas, settings) {
    this.canvas = canvas;
    this.settings = settings;
    this.leftPaddle = { x: 0, y: (canvas.height - settings.paddleHeight) / 2, width: settings.paddleWidth, height: settings.paddleHeight };
    this.rightPaddle = { x: canvas.width - settings.paddleWidth, y: (canvas.height - settings.paddleHeight) / 2, width: settings.paddleWidth, height: settings.paddleHeight };
    this.ball = { x: canvas.width / 2, y: canvas.height / 2, radius: settings.ballRadius, velocityX: 0, velocityY: 0 };
    this.leftScore = 0;
    this.rightScore = 0;
    this.gameOver = false;
    this.paused = false;
    this.maxScore = settings.maxScore || 5;
    this.deltaTime = 0;
    this.lastTime = Date.now();
  }

  reset() {
    // Reset game state
    this.leftPaddle.y = (this.canvas.height - this.settings.paddleHeight) / 2;
    this.rightPaddle.y = (this.canvas.height - this.settings.paddleHeight) / 2;
    this.ball.x = this.canvas.width / 2;
    this.ball.y = this.canvas.height / 2;
    this.ball.velocityX = Math.random() < 0.5 ? -this.settings.ballSpeed : this.settings.ballSpeed;
    this.ball.velocityY = Math.random() * 2 - 1; // Random vertical speed
    this.gameOver = false;
    this.paused = false;

    // Reset scores
    this.leftScore = 0;
    this.rightScore = 0;
  }

  update() {
    if (this.paused || this.gameOver) return;

    const now = Date.now();
    const elapsedTime = now - this.lastTime;

    // Update delta time for smooth movement
    if (elapsedTime > 0) {
      this.deltaTime += elapsedTime / (1000 / 60); // Assuming a target frame rate of 60 FPS
      if (this.deltaTime > 1) this.deltaTime = 1; // Clamp to avoid large jumps
    }

    // Update ball position
    this.ball.x += this.ball.velocityX * this.deltaTime;
    this.ball.y += this.ball.velocityY * this.deltaTime;

    // Check for wall collisions
    if (this.ball.y <= 0 || this.ball.y >= this.canvas.height) {
      this.ball.velocityY *= -1; // Reverse Y velocity on collision
    }

    // Check for paddle collisions
    if (this._checkPaddleCollision(this.leftPaddle) || this._checkPaddleCollision(this.rightPaddle)) {
      this.ball.velocityX *= -1; // Reverse X velocity on collision
    }

    // Check for scoring conditions
    if (this.ball.x < 0) {
      this.rightScore++;
      if (this.rightScore >= this.maxScore) {
        this.gameOver = true;
      } else {
        this.reset();
      }
    } else if (this.ball.x > this.canvas.width) {
      this.leftScore++;
      if (this.leftScore >= this.maxScore) {
        this.gameOver = true;
      } else {
        this.reset();
      }
    }

    // Update last time for next frame
    this.lastTime = now;
  }

  _checkPaddleCollision(paddle) {
    return (
      this.ball.x + this.ball.radius > paddle.x &&
      this.ball.x - this.ball.radius < paddle.x + paddle.width &&
      this.ball.y + this.ball.radius > paddle.y &&
      this.ball.y - this.ball.radius < paddle.y + paddle.height
    );
  }
}

